<template>    <div>        <!--        //搜索-->        <form>            <van-search                    v-model="seaTxt"                    background="#3194ff"                    show-action                    @input="searchlx"                    placeholder="请输入搜索关键词"                    @search="onSearch"                    @cancel="$router.back()"            />        </form>        <div class="conent">            <!--        历史记录-->            <div class="history" v-if="!seaTxt">                <div class="histitle">                    <span>历史记录</span>                    <van-icon name="delete" class="hisicon" @click="hisDel"/>                </div>                <van-row class="hiscont">                    <van-col class="hisspan" span="12" v-for="(item,index) in hisList" :key="index">{{item}}</van-col>                </van-row>            </div>            <!--        联想词-->            <div class="assword" v-else>                <van-cell :title="item" icon="search" v-for="(item,index) in lxList" :key="index"/>            </div>        </div>    </div></template><script>  import { assword, hisdel, histories } from '@/api/search.js'  export default {    name: 'search',    data () {      return {        seaTxt: '',        lxList: [],        hisList: []      }    },    methods: {      //搜索回车事件      onSearch (val) {        this.$toast(val)      },      //搜索联想      async searchlx () {        // window.console.log(this.seaTxt)        let msg = await assword({ q: this.seaTxt })        this.lxList = msg.data.options      },      //删除历史记录      async hisDel () {        await hisdel()        this.hisList = []      }    },    created () {      histories().then(msg => {        this.hisList = msg.data.keywords      })    }  }</script><style scoped lang="less">    .van-search__action {        color: #fff;    }    .conent {        background: #fff;        .history {            .histitle {                display: flex;                justify-content: space-between;                align-items: center;                height: 44px;                padding: 0 10px;                font-size: 14px;                .hisicon {                    font-size: 22px;                }            }        }        .hiscont {            .hisspan {                display: flex;                align-items: center;                justify-content: center;                height: 44px;                font-size: 16px;                border-top: .5px solid #e8e8e8;                &:nth-child(odd) {                    border-right: .5px solid #e8e8e8;                }            }        }    }</style>